import "./styles/styles-global.css"
import { useState } from "react";

import PostList from "./components/UI/PostItem/PostList";
import Button from "./components/UI/button/Button";
import Input from "./components/UI/input/Input";

function App() {

  const [posts, setPosts] = useState([

    { id: 1, title: 'Javascript', body: 'Description' },
    { id: 2, title: 'C++', body: 'Description' },
    { id: 3, title: 'C#', body: 'Description' },
    { id: 4, title: 'Python', body: 'Description' },
    { id: 5, title: 'TypeScript', body: 'Description' },

  ])
  const [title, setTitle] = useState('')
  const [body, setBody] = useState('')

  const addNewPost = (e) => {

    e.preventDefault()
    console.log(title)
    console.log(body)

  }

  return (
    <div className="App">
      <form>
        <Input
          value={title}
          onChange={e => setTitle(e.target.value)}
          type="text"
          placeholder="Post Name" />
        <Input
          value={body}
          onChange={e => setBody(e.target.body)}
          type="text"
          placeholder="Post Description" />
        <Button onClick={addNewPost}> Create </Button>
      </form>

      <PostList posts={posts} title="List of Post 1" />
    </div>
  );
};

export default App;
